import React from "react";

export default class Home extends React.Component {
  constructor(props) {
    super(props);
    this.state = { query: "", submitted: false, volumes: [] };
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleChange = this.handleChange.bind(this);
  }
  handleChange(event) {
    const value = event.target.value;
    this.setState({ query: value });
  }

  handleSubmit(event) {
    event.preventDefault();
    this.props.hisory.replace(`list/${this.state.query}`);
    this.setState({ submitted: true });
  }

  getActions = () => {
    return {
      submitQuery: this.handleSubmit,
      typeLetter: this.handleChange
    };
  };

  render() {
    return this.props.render({ ...this.state }, { actions: this.getActions() });
  }
}
