import axios from "axios";
//  'https://www.googleapis.com/books/v1/volumes?q=baseball&key=[YOUR_API_KEY]' \

const KEY = process.env.REACT_APP_API_KEY;
const url = "https://googleapis.com/books/v1/";

export async function getVolumes(query) {
  const request = `${url}volumes?q=${query}&key=${KEY}`;
  try {
    const data = await axios.get(request, {
      headers: { Accept: "application/json" }
    });
    return data;
  } catch (err) {
    console.warn(err);
  }
}
