import React from "react";
import CoverImg from "../CoverImg";
import Stars from "./Stars";
import { Link } from "react-router-dom";
import PropTypes from "prop-types";

const CardView = ({ title, author, imageLinks, averageRating, id }) => {
  return (
    <div className="card">
      <Link to={{ pathname: "/volume", search: `id=${id}` }}>
        <div>
          <h4>{title}</h4>
          <h5>{`${author}`}</h5>
          <div className="thumbnailContainer">
            <CoverImg volumeInfo={imageLinks} maxRes={false} />
          </div>
          <div className="stars">
            {averageRating && <Stars averageRating={averageRating} />}
          </div>
        </div>
      </Link>
    </div>
  );
};

//render prop Card should passes props to render prop to be consumed by List and Volume
class Card extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      volume: this.props.volume,
      volumeInfo: this.props.volume.volumeInfo
    };
  }

  getProps = () => {
    const volume = this.state.volume;
    const volumeInfo = this.state.volumeInfo;
    return {
      id: volume.id,
      title: volumeInfo.title ? volumeInfo.title : "",
      author: volumeInfo.authors ? volumeInfo.authors[0] : "",
      averageRating: volumeInfo.averageRating ? volumeInfo.averageRating : "",
      imageLinks: volumeInfo.imageLinks ? volumeInfo.imageLinks : ""
    };
  };
  render() {
    return <CardView {...this.getProps()} />;
  }
}

Card.propTypes = {
  volume: PropTypes.object.isRequired
};

export default Card;
