import React from "react";

class CoverImg extends React.Component {
  state = { src: "", noImage: false };
  render() {
    const { volumeInfo } = this.props;
    if (!volumeInfo.imageLinks) {
      this.setState({
        noImage: true,
        src:
          "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcS_KU_zDyZNz9WZd-rIoe5lKKJoXBLgdPnCEFqFWhf_-2i9iKds"
      });
    }
    //get the image with highest resolution
    if (!this.state.noImage) {
      const imgArr = Object.keys(volumeInfo.imageLinks);
      console.log("volumeInfo keys", imgArr);
      this.setState({ src: imgArr[imgArr.length - 1] });
    }
    return <img src="this.state.src" alt="book cover" />;
  }
}

export default CoverImg;
