import React from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faStar, faStarHalf } from "@fortawesome/free-solid-svg-icons";

export default class Stars extends React.Component {
  renderFullStars = count => {
    return [...Array(count)].map((star, index) => (
      <span>
        <FontAwesomeIcon icon={faStar} />
      </span>
    ));
  };
  renderPartialStars = remainder => {
    if (remainder) {
      return (
        <span>
          <FontAwesomeIcon icon={faStarHalf} style={{ color: "yellow" }} />
        </span>
      );
    }
  };
  renderEmptyStars = count => {
    for (let i = 0; i < count; i++) {
      return (
        <span>
          <FontAwesomeIcon icon={faStar} style={{ color: "none" }} />
        </span>
      );
    }
  };

  renderStars = averageRating => {
    const rating = Number(averageRating);
    //count of full stars
    const full = Number.parseInt(rating);
    //boolean true to render partial star
    const partial = rating % 1 !== 0;
    // count of empty stars
    const empty = partial ? 4 - full : 5 - full;
    return (
      <>
        <span className="fullstars">{this.renderFullStars(full)}</span>
        <span>{this.renderPartialStars(partial)}</span>
        <span>{this.renderEmptyStars(empty)}</span>
      </>
    );
  };

  render() {
    const { averageRating } = this.props;

    return <>{this.renderStars(averageRating)}</>;
  }
}
