import React, { useState } from "react";
import moment from "moment";
import CoverImg from "../CoverImg";
import PropTypes from "prop-types";
import Stars from "../card/Stars";
import { useSpring, animated as a } from "react-spring";

function CardFlipper({ description, previewLink, imageLinks }) {
  const [flipped, set] = useState(false);
  const { transform, opacity } = useSpring({
    opacity: flipped ? 1 : 0,
    transform: `perspective(600px) rotateX(${flipped ? 180 : 0}deg)`,
    config: { mass: 5, tenstion: 500, friction: 80 }
  });
  return (
    <div onClick={() => set(state => !state)}>
      <a.div
        class="c back"
        style={{ opacity: opacity.interpolate(o => 1 - o), transform }}>
        <Back description={description} previewLink={previewLink} />
      </a.div>
      <a.div
        class="c front"
        style={{
          opacity,
          transform: transform.interpolate(t => `${t} rotateX(180deg)`)
        }}>
        <Front imageLinks={imageLinks} />
      </a.div>
    </div>
  );
}

const Catagories = ({ categories }) => {
  return (
    <p>
      {categories.map(e => (
        <span key={Math.random() * 20}>{e} </span>
      ))}
    </p>
  );
};

Catagories.propTypes = {
  catagories: PropTypes.array
};

const Front = ({ imageLinks }) => {
  return <CoverImg imageLinks={imageLinks} maxRes={true} width={"350px"} />;
};

const Back = ({ description, previewLink }) => {
  return (
    <>
      <div className="description">
        <p dangerouslySetInnerHTML={{ __html: `${description}` }} />
      </div>
      <div />
      <a href={previewLink}>
        <button>View at Google Books</button>
      </a>
    </>
  );
};

const hide = { opacity: 0 };
const show = { opacity: 1 };

class VolumeCardView extends React.Component {
  state = { bookFlipped: false };
  flipperClick = () =>
    this.setState(state => ({ bookFlipped: !state.bookFlipped }));
  render() {
    const { bookFlipped } = this.state;
    const {
      title,
      author,
      publishedDate,
      imageLinks,
      averageRating,
      description,
      pageCount,
      previewLink,
      categories
    } = this.props;
    return (
      <div className="volumeContainer">
        <div>
          <div className="miniHeader">
            <div className="titleInfo">
              <h5>{title}</h5>
              <p>{moment(publishedDate).format("Y")}</p>
              <div>
                {averageRating && <Stars averageRating={averageRating} />}
              </div>
            </div>

            <div className="volumeAuthor">
              <h4>{author}</h4>
            </div>
            <div className="titleMeta">
              <p>{pageCount} pages</p>
              {categories && <Catagories categories={categories} />}
            </div>

            <div className="imgWrapper">
              <Front imageLinks={imageLinks} />
            </div>
            <div className="imgWrapper">
              <Back description={description} previewLink={previewLink} />
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default VolumeCardView;
