import React from "react";
import { Link } from "react-router-dom";
import CoverImg from "./CoverImg";

class Card extends React.Component {
  state = { id: this.props.id, volume: this.props.volume };

  render() {
    const { id, volume } = this.state;
    const { title, authors } = volume.volumeInfo;
    return (
      <div className="cardContainer">
        <li key={id}>
          <div className="card">
            <Link to={{ pathname: "/volume", search: `id=${id}` }}>
              <div>
                <h4>{title}</h4>
                <h5>by {authors[0]}</h5>
                <div className="thumbnailContainer">
                  <CoverImg volumeInfo={volume.volumeInfo} maxRes={false} />
                </div>
              </div>
            </Link>
          </div>
        </li>
      </div>
    );
  }
}

export default Card;
